import moment from "moment";
import { useState } from "react";

function Calendar() {
    const [year, changeYear] = useState(2022);
    const [month, changeMonth] = useState(6);

    const startOfMonth = moment().year(year).month(month).startOf("month");

    // const monthSize = parseInt(moment().year(2022).month(5).endOf("month").format("DD"));
    console.log(`Start of Month : ${startOfMonth}`);
    // console.log(`End of Month : ${monthSize}`);
}

export default Calendar();
