import moment from "moment";
import React, { useState } from "react";

function Calendar() {
    const year = 2022;
    const month = 5; // 6월꺼를 보고 싶으면 monthdp 5을 입력해야함.

    const startOfMonth = moment().year(year).month(month).startOf("month").format("e");
    const endOfMonth = moment().year(year).month(month).endOf("month").format("e");
}

export default Calendar();
