import moment from "moment";
import { weekdayArray } from "../constant/constants.js";
import { CalendarContainer, CalendarDayContainer, CalendarWeekContainer, CalendarWeekDayContainer } from "../styledComponent/style.js";
import _ from "lodash";

function MyCalendar() {
    const year = 2022;
    const month = 6;

    const startWeekday = moment().year(year).month(month).startOf("month").format("e");
    const endWeekday = moment().year(year).month(month).endOf("month").format("e");
    const endDate = parseInt(moment().year(year).month(month).endOf("month").format("DD"));

    const firstBlank = _.range(0, startWeekday);
    const lastBlank = _.range(0, 6 - endWeekday);
    const dayArray = _.range(1, endDate + 1);

    return (
        <>
        <Header>2022-06</Header>
        <CalendarContainer>
            <CalendarWeekContainer>
                {weekdayArray.map((weekday, idx) => <CalendarWeekDayContainer key={idx}>{weekday}</CalendarWeekDayContainer>
                )}
                {firstBlank.map((day, idx) => <CalendarDayContainer key={idx}>{}</CalendarDayContainer>)}
                {dayArray.map((day, idx) => <CalendarDayContainer key={idx}>{day}</CalendarDayContainer>)}
                {lastBlank.map((day, idx) => <CalendarDayContainer key={idx}>{}</CalendarDayContainer>)}

            </CalendarWeekContainer>

        </CalendarContainer>
        </>

    );
}

export default MyCalendar;
