import styled from "styled-components";
import { BiChevronLeft, BiChevronRight } from "react-icons/bi";

export const CalendarContainer = styled.div`
    display: flex;
    justify-content: center;
    align-item: center;
`;

export const CalendarWeekContainer = styled.div`
    display: grid;
    width: 80vw;
    max-width: 1000px;
    margin-top: 4rem;
    grid-template-columns: repeat(7, auto);
`;

export const CalendarWeekDayContainer = styled.div`
    height: 2rem;
    color: #000;
    text-align: center;
    line-height: 2rem;
`;

export const CalendarDayContainer = styled.div`
    border: 1px solid #dedede;
    height: 5rem;
    color: #000;
    text-align: right;
    padding: 10px;
`;

export const Header = styled.div`
    text-align: center;
    margin-bottom: 0;
    display: flex;
    justify-content: center;
    margin-top: 20px;
`;

export const LeftButton = styled.BiChevronLeft`
    size: 70;
`;
export const RightButton = styled.BiChevronRight`
    size: 70;
`;
